kickstart.post:
/bin/mkdir -m 0755 /root/rolltests


#!/usr/bin/perl -w
# beast roll installation test.  Usage:
# beast.t [nodetype]
#   where nodetype is one of "Compute", "Dbnode", "Frontend" or "Login"
#   if not specified, the test assumes either Compute or Frontend

use Test::More qw(no_plan);

my $appliance = $#ARGV >= 0 ? $ARGV[0] :
                -d '/export/rocks/install' ? 'Frontend' : 'Compute';
my $installedOnAppliancesPattern = 'Compute';
my $isInstalled = -d '/opt/beast';
my $output;

# beast-doc.xml
SKIP: {
  skip 'not server', 1 if $appliance ne 'Frontend';
  ok(-d '/var/www/html/roll-documentation/beast', 'doc installed');
}

# beast-install.xml
if($appliance =~ /$installedOnAppliancesPattern/) {
  ok($isInstalled, 'beast installed');
} else {
  ok(! $isInstalled, 'beast not installed');
}
SKIP: {
  skip 'beast not installed', 1 if ! $isInstalled;
  $output = `java -jar /opt/beast/lib/beast.jar /opt/beast/examples/new_benchmark.xml 2>&1`;
  ok($output =~ /Operator analysis/ && $output =~ /\d+\.\d+ seconds\s*$/,
     'beast benchmark run');
  `/bin/rm -f mcmc.operators new_benchmark.log new_benchmark.trees`;
}

# beast-modules-common
SKIP: {

  skip 'beast not installed', 1 if ! $isInstalled;
  my $modulesInstalled = -f '/etc/profile.d/modules.sh';
  if($modulesInstalled) {
    ok(! -f '/etc/profile.d/beast.csh' &&
       ! -f '/etc/profile.d/beast.sh', 'beast profile.d removed');
    like($ENV{MODULEPATH}, qr#/opt/modulefiles/applications#,
         'MODULEPATH set for applications');
    ok(defined(glob('/opt/modulefiles/applications/beast/[0-9]*')),
       'beast module installed');
    ok(defined(glob('/opt/modulefiles/applications/beast/.version.[0-9]*')),
       'beast version module installed');
    ok(-l '/opt/modulefiles/applications/beast/.version',
       'beast version module link created');
  } else {
    ok(-f '/etc/profile.d/beast.csh' && -f '/etc/profile.d/beast.sh',
       'beast profile.d installed');
  }

}
